C12832 lcd(p5, p7, p6, p8, p11);
int xLoc = 0; // Default coordinates
int yLoc = 0; //

Bitmap backgroundBM = {128, 32, 16, groundWSun};
// Display size: 128x32
Bitmap backgroundBMd = {128, 32, 16, groundWSunD};

void printBackgroundL() {
  lcd.print_bm(backgroundBM, xLoc, yLoc);
}

void printBackgroundD() {
  lcd.print_bm(backgroundBMd, xLoc, yLoc);

}
void printBackG() {
  if (ain.read() > 0.0 && ain.read() < 0.5) {
    printBackgroundL();
  } else {
    printBackgroundD();
  }
lcd.copy_to_lcd();
}

